import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { busca } from "../api/api";
import '../assets/css/blog.css'
const  ListaCategorias= () =>{
    const[categorias, setCategorias] = useState([])//Set o estado inicial da aplicação
    useEffect(() =>{
        busca(`/categorias`, setCategorias)//onde vai mandar o arquivo
    }, []) //comunicação do api
    return(
        // parte que vai mostrar na tela
        <ul className = 'lista-categorias container flex'>
            {
                categorias.map((categoria) => (
                    <Link to={`categoria/${categoria.id}`}> 
                        <li className ={`lista-categorias__categoria
                        lista-categorias__categoria--${categoria.id}`}></li>
                        {categoria.nome}
                    </Link>
                ))//loop de interação mapeando 
            }
        </ul>
    )

}
export default ListaCategorias
