import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { busca } from "../api/api";

const ListPost = ({url} ) => {

const [posts, setPosts] = useState([])//novo

   useEffect(() => { 
    busca(url, setPosts)
    }, []) 
    
    return(
        <section>
            {
            posts.map((post)=> (//novo
                <Link className = {`cartao-post cartao-post--${post.categoria}`}>
                    <article Key ={post.id}>
                        <h3 className ="cartao-post_titulo"> 
                            {post.title}
                        </h3>
                        <p className="cartao-post_meta">{post.metadescription}</p>
                    </article>
                </Link>
            ))
            }
        </section>
    )
}

export default ListaPost
